// Reason function returns reason value.
func (e DisplayResourceValidationError) Reason() string { return e.reason }

// Cause function returns cause value.
func (e DisplayResourceValidationError) Cause() error { return e.cause }

// Key function returns key value.
func (e DisplayResourceValidationError) Key() bool { return e.key }

// ErrorName returns error name.
func (e DisplayResourceValidationError) ErrorName() string { return "DisplayResourceValidationError" }

// Error satisfies the builtin error interface
func (e DisplayResourceValidationError) Error() string {
	cause := ""
	if e.cause != nil {
		cause = fmt.Sprintf(" | caused by: %v", e.cause)
	}

	key := ""
	if e.key {
		key = "key for "
	}

	return fmt.Sprintf(
		"invalid %sDisplayResource.%s: %s%s",
		key,
		e.field,
		e.reason,
		cause)
}

var _ error = DisplayResourceValidationError{}

var _ interface {
	Field() string
	Reason() string
	Key() bool
	Cause() error
	ErrorName() string
} = DisplayResourceValidationError{}
